            <ComicBookNoGeo background={state.background} text={state.text} channel={state.id}/>
        </div>
    )
}else if(state.glitchtype === "ComicBookText"){
    return (
        <div>
            <ComicBookEffectText background={state.background} text={state.text} channel={state.id}/>
        </div>
    )
}

}

