let ENTER = 13
var getKey = function(e) {
    if(window.event) { return e.keyCode; }  // IE
    else if(e.which) { return e.which; }    // Netscape/Firefox/Opera
  };


  var keynum = getKey(e);

  if(keynum === UP) {
